and `room_state`!=2

order by `room_id` desc limit #{offset}, 10;

select * from `lemo_product_reservation` as a
    inner join `lemo_product_room` as b on a.room_id=b.room_id
    inner join `lemo_product_accommodation` as c on a.acc_id=c.acc_id
where c.`user_id`=#{user_id}

    and a.`acc_id`=#{acc_id}

order by `res_state` asc limit ${offset}, 10;

select count(*) from `lemo_product_reservation` as a
inner join `lemo_product_room` as b on a.room_id=b.room_id
inner join `lemo_product_accommodation` as c on a.acc_id=c.acc_id
where c.`user_id`=#{user_id}

    and a.`acc_id`=#{acc_id}

SELECT DATE(a.res_date) AS res_date,
    COUNT(`res_no`) as count_sales,
    (SUM(a.res_price - a.res_disPrice)) AS tot_res_price
FROM lemo_product_reservation AS a
    LEFT JOIN lemo_product_accommodation AS b ON a.acc_id = b.acc_id
WHERE a.res_state NOT IN (0) AND a.res_checkIn BETWEEN DATE(date_add(now(), INTERVAL 0 DAY))
    AND DATE(NOW())
    AND b.user_id=#{user_id}

        and b.`acc_id`=#{acc_id}

GROUP BY DATE(a.res_date);

select a.`res_no`, `revi_title`, `revi_content`, `revi_rate`, `revi_rdate`, c.`acc_id`, c.`user_id` from `lemo_product_review` as a
    join `lemo_product_reservation` as b on a.res_no=b.res_no
    join `lemo_product_accommodation` as c on b.acc_id=c.acc_id
where c.`user_id`=#{user_id}
order by `revi_rdate` desc
limit 5;

SELECT DATE(a.res_date) AS res_date, MAX(a.res_state) AS res_state, SUM(a.res_price) AS res_price, SUM(a.res_disPrice) AS res_disPrice, (SUM(a.res_price - a.res_disPrice)) AS tot_res_price FROM lemo_product_reservation AS a LEFT JOIN lemo_product_accommodation AS b ON a.acc_id = b.acc_id WHERE a.res_state NOT IN (0)
AND a.`res_checkIn`
AND a.`res_checkIn`
AND a.`res_checkIn`
AND a.`res_checkIn`
AND a.`res_checkIn`
AND b.user_id=#{user_id}
AND a.acc_id=#{acc_id}
GROUP BY DATE(a.res_date) ORDER BY `res_date` desc;

SELECT yrs.res_date, COALESCE(yrs.res_state, 0) AS res_state, COALESCE(yrs.res_price, 0) AS res_price, COALESCE(yrs.res_disPrice, 0) AS res_disPrice, COALESCE(yrs.tot_res_price, 0) AS tot_res_price FROM ( SELECT YEAR(CURDATE() - INTERVAL 2 YEAR) AS res_date UNION SELECT YEAR(CURDATE() - INTERVAL 1 YEAR) AS res_date UNION SELECT YEAR(CURDATE()) AS res_date ) AS years LEFT JOIN ( SELECT YEAR(a.res_date) AS res_date, MAX(a.res_state) AS res_state, SUM(a.res_price) AS res_price, SUM(a.res_disPrice) AS res_disPrice, (SUM(a.res_price - a.res_disPrice)) AS tot_res_price FROM lemo_product_reservation AS a LEFT JOIN lemo_product_accommodation AS b ON a.acc_id = b.acc_id WHERE b.`user_id`=#{user_id} AND a.res_state NOT IN (0) AND a.res_checkIn BETWEEN DATE(date_add(now(), INTERVAL -2 YEAR)) AND DATE(NOW())
AND b.acc_id=#{acc_id}
GROUP BY YEAR(a.res_date) ) AS yrs ON years.res_date = yrs.res_date ORDER BY years.res_date;

select * from `lemo_product_reservation` as a join `lemo_product_accommodation` as b on a.acc_id=b.acc_id where a.res_state NOT IN (0)
AND a.`res_checkIn`
AND a.`res_checkIn`
AND a.`res_checkIn`
AND a.`res_checkIn`
AND a.`res_checkIn`
AND b.user_id=#{user_id}
AND a.acc_id=#{acc_id}
order by a.res_payment;

SELECT months.res_month, COALESCE(totals.tot_res_price, 0) AS tot_res_price, COALESCE(totals.tot_month_sales, 0) AS tot_month_sales FROM ( SELECT 1 AS res_month UNION SELECT 2 UNION SELECT 3 UNION SELECT 4 ) AS months LEFT JOIN ( SELECT MONTH(a.res_date) AS res_month, SUM(a.res_price - a.res_disPrice) AS tot_res_price, COUNT(a.`res_checkIn`) AS tot_month_sales FROM lemo_product_reservation AS a LEFT JOIN lemo_product_accommodation AS b ON a.acc_id = b.acc_id WHERE b.`user_id`=#{user_id} AND a.res_state NOT IN (0) AND a.res_checkIn BETWEEN DATE(date_add(now(), INTERVAL -4 MONTH)) AND DATE(NOW())
AND b.acc_id=#{acc_id}
GROUP BY MONTH(a.res_date) ) AS totals ON months.res_month = totals.res_month ORDER BY months.res_month;

select a.`room_id`, b.`room_name`, COUNT(a.`room_id`) as count_sales, SUM(a.`res_price` - a.`res_disPrice`) as tot_res_price, b.`room_stock` from `lemo_product_reservation` as a inner join `lemo_product_room` as b on a.room_id=b.room_id inner join `lemo_product_accommodation` as c on a.acc_id=c.acc_id where a.res_state NOT IN (0)
AND a.`res_checkIn`
AND a.`res_checkIn`
AND a.`res_checkIn`
AND a.`res_checkIn`
AND a.`res_checkIn`
and c.`user_id`=#{user_id}
and a.`acc_id`=#{acc_id}
group by a.`room_id`;

select count(room_id) from `lemo_product_room` as a
    join `lemo_product_accommodation` as b on a.acc_id=b.acc_id
where b.user_id=#{user_id};

SELECT count(acc_id) FROM `lemo_product_accommodation` WHERE user_id=#{user_id}
AND `acc_rdate`
AND `acc_rdate`
AND `acc_rdate`
AND `acc_rdate`
AND acc_id=#{acc_id};

select * from `lemo_product_reservation` as a inner join `lemo_product_room` as b on a.room_id=b.room_id inner join `lemo_product_accommodation` as c on a.acc_id=c.acc_id where c.`user_id`=#{user_id}
and month(a.`res_checkIn`) = month(current_date()) and year(a.`res_checkIn`) = year(current_date())
and c.`acc_id`=#{acc_id}

select a.*, b.`acc_name`, c.`nick` from `lemo_product_qna` as a
join `lemo_product_accommodation` as b on a.acc_id=b.acc_id
JOIN `lemo_member_userinfo` AS c ON a.user_id=c.user_id
where b.`user_id`= #{user_id}

    and a.`acc_id`=#{acc_id}

order BY a.`qna_no` DESC LIMIT #{offset}, 10;

select count(*) FROM `lemo_product_qna` AS a
JOIN `lemo_product_accommodation` AS b
ON a.acc_id = b.acc_id
WHERE b.`user_id` = #{user_id}

    and a.`acc_id`=#{acc_id}
