mapper.select:
SELECT a.`province_no`, a.`acc_name`, a.`acc_id`, a.`acc_thumbs`, b.`res_checkIn`, b.`res_checkOut`, b.`res_date`, b.`res_no`, IFNULL(c.`revi_id`, 0) AS reviewStat FROM `lemo_product_accommodation` AS a LEFT JOIN `lemo_product_reservation` AS b ON a.`acc_id` = b.`acc_id` LEFT JOIN `lemo_product_review` AS c ON b.`res_no` = c.`res_no` WHERE b.`user_id` = #{user_id} AND b.`res_state` = 2
AND c.`revi_id` IS NOT null AND c.`revi_id` IS null
ORDER BY `res_date` ASC LIMIT #{offset}, 10;

mapper.insert:
INSERT INTO `lemo_member_coupon` (`cp_id`, `user_id`, `mcp_start`, `mcp_end`, `mcp_rdate`)
SELECT #{cp_id}, #{user_id}, NOW(), DATE_ADD(NOW(), INTERVAL `cp_daysAvailable` DAY), NOW()
FROM `lemo_product_coupon` WHERE `cp_id` = #{cp_id};
