mapper.select:
SELECT a.`province_no`, a.`acc_name`, a.`acc_id`, a.`acc_thumbs`, b.`res_checkIn`, b.`res_checkOut`, b.`res_date`, b.`res_no`, IF(c.`revi_id` IS NULL, 0, 1) AS reviewStat FROM `lemo_product_accommodation` AS a LEFT JOIN `lemo_product_reservation` AS b ON a.`acc_id` = b.`acc_id` LEFT JOIN `lemo_product_review` AS c ON b.`res_no` = c.`res_no` WHERE b.`user_id` = #{user_id} AND b.`res_state` = 2
AND c.`revi_id` IS NOT null AND c.`revi_id` IS null
ORDER BY `res_date` ASC LIMIT #{offset}, 10;

SELECT a.*, b.`acc_name` FROM `lemo_product_qna` AS a
JOIN `lemo_product_accommodation` AS b
ON a.`acc_id` = b.`acc_id`
WHERE a.`user_id` = #{user_id}
ORDER BY `qna_no` DESC
LIMIT #{offset}, 10;

SELECT COUNT(*) FROM `lemo_member_userinfo` u JOIN
`lemo_member_coupon` c ON u.user_id = c.user_id
WHERE u.`user_id`= #{user_id};

mapper.insert:
INSERT INTO `lemo_member_coupon` (`cp_id`, `user_id`, `mcp_start`, `mcp_end`, `mcp_rdate`)
SELECT #{cp_id}, #{user_id}, NOW(), DATE_ADD(NOW(), INTERVAL `cp_daysAvailable` DAY), NOW()
FROM `lemo_product_coupon` WHERE `cp_id` = #{cp_id};


    SELECT `poid_id` FROM `lemo_member_point_detail_log` ORDER BY `poid_id` DESC LIMIT 1;


INSERT INTO `lemo_member_point_detail_log` (`poid_id`, `user_id`, `poid_content`, `poid_point`, `poi_id`, `poid_save_id`, `poid_maximum`, `poid_rdate`)
SELECT #{poid_id} + 1, `user_id`, `poi_content`, `poi_point`, `poi_id`, #{poid_id} + 1, `poi_maximum`, NOW()
FROM `lemo_member_point_log`
WHERE `poi_id`=#{poi_id};


INSERT INTO `lemo_member_point_detail_log` (`user_id`, `poid_content`, `poid_point`, `poid_save_id`, `poi_id`, `poid_maximum`, `poid_rdate`)
SELECT val.`user_id`, '포인트 만료' AS 'poid_content', -val.`sum` AS 'poid_point', val.`poid_save_id`, val.`poi_id`, val.`poid_maximum`, NOW()  FROM (
SELECT *, SUM(`poid_point`) AS 'sum' FROM `lemo_member_point_detail_log`
WHERE `user_id`=#{user_id}
GROUP BY `poid_save_id`
HAVING SUM(`poid_point`) != 0 AND `poid_maximum` < DATE_FORMAT(NOW(),'%Y-%m-%d')
) val;

mapper.update:
UPDATE `lemo_member_userinfo` user JOIN
(
SELECT `user_id`, SUM(`poid_point`) AS 'total'
FROM `lemo_member_point_detail_log`
WHERE `user_id`=#{user_id}
) log ON user.user_id = log.user_id
SET
user.`point` = log.`total`;

UPDATE `lemo_member_coupon` c
JOIN
(
SELECT mcp_id FROM  `lemo_member_coupon_log`
WHERE `res_no`= #{res_no} AND `user_id`=#{user_id}
) cl ON c.mcp_id = cl.mcp_id
SET
c.mcp_isUsed = 0;
