mapper.select:
SELECT a.*, r.*, IFNULL(b.empty_room_stock, r.room_stock) as 'empty_room_stock',c.* FROM `lemo_product_accommodation` a JOIN `lemo_product_room` r ON a.acc_id = r.acc_id LEFT JOIN ( SELECT a.acc_id 'acc_id', r.room_id 'room_id', (r.room_stock - COUNT(rero.rero_id)) 'empty_room_stock' FROM `lemo_product_accommodation` a JOIN `lemo_product_room` r ON a.acc_id = r.acc_id JOIN `lemo_product_reserved_room` rero ON r.room_id = rero.room_id AND r.acc_id = rero.acc_id
AND (rero.rero_checkOut_date > #{checkIn} AND rero.rero_checkIn_date < #{checkOut})
GROUP BY a.`acc_id`, r.room_id ) b ON a.acc_id = b.acc_id AND r.room_id = b.room_id JOIN `lemo_product_ratepolicy` c ON a.acc_id = c.acc_id WHERE a.acc_id = #{acc_id} ORDER BY a.acc_id;
