mapper.select:
SELECT a.*, r.*, IFNULL(b.empty_room_stock, r.room_stock) as 'empty_room_stock',c.* FROM `lemo_product_accommodation` a JOIN `lemo_product_room` r ON a.acc_id = r.acc_id LEFT JOIN ( SELECT a.acc_id 'acc_id', r.room_id 'room_id', (r.room_stock - COUNT(rero.rero_id)) 'empty_room_stock' FROM `lemo_product_accommodation` a JOIN `lemo_product_room` r ON a.acc_id = r.acc_id JOIN `lemo_product_reserved_room` rero ON r.room_id = rero.room_id AND r.acc_id = rero.acc_id
AND (rero.rero_checkOut_date > #{checkIn} AND rero.rero_checkIn_date < #{checkOut})
GROUP BY a.`acc_id`, r.room_id ) b ON a.acc_id = b.acc_id AND r.room_id = b.room_id JOIN `lemo_product_ratepolicy` c ON a.acc_id = c.acc_id WHERE a.acc_id = #{acc_id} and r.room_state = 1 ORDER BY a.acc_id;

SELECT a.*, b.acc_name, c.nick FROM `lemo_product_qna` AS a JOIN `lemo_product_accommodation` AS b ON a.`acc_id` = b.`acc_id` JOIN `lemo_member_userinfo` AS c ON a.user_id = c.user_id WHERE a.`acc_id` = #{acc_id}
AND `qna_title` LIKE CONCAT('%', #{searchWord}, '%')
Order by a.`qna_no` desc LIMIT #{offset}, 10;

SELECT * FROM `lemo_member_coupon` AS a JOIN `lemo_product_coupon` AS b ON a.cp_id = b.cp_id WHERE a.`user_id` = #{user_id}
AND CURDATE() <= `mcp_end`
AND `mcp_isUsed`= 0

SELECT COUNT(`qna_no`) FROM `lemo_product_qna` WHERE `acc_id` = #{acc_id}

    AND `qna_title` LIKE CONCAT('%', #{searchWord}, '%')
;
