mapper.select:
SELECT a.*, b.acc_name, c.nick FROM `lemo_product_qna` AS a JOIN `lemo_product_accommodation` AS b ON a.`acc_id` = b.`acc_id` JOIN `lemo_member_userinfo` AS c ON a.user_id = c.user_id WHERE a.`acc_id` = #{acc_id}
AND `qna_title` LIKE CONCAT('%', #{searchWord}, '%')
Order by a.`qna_no` desc LIMIT #{offset}, 10;

SELECT * FROM `lemo_member_coupon` AS a JOIN `lemo_product_coupon` AS b ON a.cp_id = b.cp_id WHERE a.`user_id` = #{user_id}
AND CURDATE() <= `mcp_end`
AND `mcp_isUsed`= 0

SELECT COUNT(`qna_no`) FROM `lemo_product_qna` WHERE `acc_id` = #{acc_id}

    AND `qna_title` LIKE CONCAT('%', #{searchWord}, '%')
;

mapper.update:
UPDATE `lemo_member_userinfo` user JOIN
(
SELECT `user_id`, SUM(`poid_point`) AS 'total'
FROM `lemo_member_point_detail_log`
WHERE `user_id`=#{user_id}
) log ON user.user_id = log.user_id
SET
user.`point` = log.`total`;
